    @RequestMapping(value = "/project/projectList", method = RequestMethod.POST)
    @ResponseBody
    public Map<String, Object> projectInfoQuery(ProjectInfoParams params) {

        Map<String, Object> result = new HashMap<String, Object>();

        int start = (params.getPage() - 1) * params.getRows();
        List<ProjectInfo> rows = new ArrayList<ProjectInfo>();

        int total = projectService.queryCount(params);

        if (total == 0) {
            result.put("rows", rows);
            result.put("total", total);
            return result;
        }
        rows = projectService.queryList(params, start);

        result.put("total", total);
        result.put("rows", rows);
        return result;
    }

