    /**
     * 获取数据模板信息
     *
     * @param model
     * @param projectInfoId
     * @param projectName
     */
    private Boolean getProjectParam(Model model, Integer projectInfoId, String projectName) {
        Boolean isGroupTemplateFlg = false;
        if (projectService.isGroupTemplate(projectInfoId)) {
            List<ProjectGroupParamBean> projectGroupParamBeanList = projectService.getGroupParam(projectInfoId);
            model.addAttribute("groupParam", projectGroupParamBeanList);
            model.addAttribute("isGroupTemplate", true);
            isGroupTemplateFlg = true;
        } else {
            Map<String, List<ProjectParam>> parMap = projectService.getProjectParam(projectInfoId);
            List<ProjectParam> inparList = parMap.get("inParList");
            List<ProjectParam> outparList = parMap.get("outParList");
            model.addAttribute("inparList", inparList);
            model.addAttribute("outparList", outparList);
            if (inparList.size() > 0 && outparList.size() > 0) {
                model.addAttribute("isNormalTemplate", true);
            } else {
                model.addAttribute("initialTemplate", true);
            }

        }
        try {
            projectName = URLDecoder.decode(projectName, "UTF-8");
            model.addAttribute("projectName", projectName);
        } catch (UnsupportedEncodingException e) {
            e.printStackTrace();
        }
        return isGroupTemplateFlg;
    }

