    /**
     * 编辑项目模板数据
     *
     * @param model
     * @param projectInfoId
     * @return
     */
    @RequestMapping(value = "/project/editProjectTemplateValue")
    public String editProjectTemplateValue(Model model, Integer projectInfoId, Integer paramSuitNUmber, Integer dataTemplateNumber, Integer groupDataTemplateNumber) {
        model.addAttribute("projectId", projectInfoId);

        Map<String, List<ProjectParamTemplateValueExt>> parMap = projectService.getProjectParamTemplateValue(projectInfoId, paramSuitNUmber, dataTemplateNumber, groupDataTemplateNumber);
        List<ProjectParamTemplateValueExt> inparList = parMap.get("inParList");
        List<ProjectParamTemplateValueExt> outparList = parMap.get("outParList");
        String dataName = "";
        String dataComment = "";
        Boolean flag = false;
        model.addAttribute("inparList", inparList);
        model.addAttribute("outparList", outparList);
        if (inparList.size() > 0) {
            dataName = inparList.get(0).getDataName();
            dataComment = inparList.get(0).getDataComment();
            flag = true;
        }
        if (!flag) {
            if (outparList.size() > 0) {
                dataName = outparList.get(0).getDataName();
                dataComment = outparList.get(0).getDataComment();
            }
        }
        model.addAttribute("dataName", dataName);
        model.addAttribute("dataComment", dataComment);
        model.addAttribute("paramSuitNUmber", paramSuitNUmber);
        model.addAttribute("dataTemplateNumber", dataTemplateNumber);
        model.addAttribute("groupDataTemplateNumber", groupDataTemplateNumber);
        return "project/editProjectTemplateValue";
    }

