    /**
     * 已相同父节点的节点放在一起
     *
     * @param paramList
     * @return
     */
    private List<ProjectParamParentBean> getProjectParamParentBeans(List<ProjectParamBean> paramList) {
        List<ProjectParamParentBean> list = new ArrayList<>();
        Set<Integer> set = new TreeSet<>();
        paramList.forEach(projectParamBean -> set.add(projectParamBean.getParentId()));
        for (Integer integer : set) {
//			HashMap<Integer, ProjectParamParentBean> map = new HashMap<>();
            List<ProjectParamBean> temp = new ArrayList<>();
            for (int i = 0; i < paramList.size(); i++) {
                if (paramList.get(i).getParentId().equals(integer)) {
                    temp.add(paramList.get(i));
                }
            }
            ProjectParamParentBean projectParamParentBean = new ProjectParamParentBean();
            projectParamParentBean.setProjectParamBeans(temp);
            list.add(projectParamParentBean);
        }
        return list;
    }

