    /**
     * 查看项目标签
     *
     * @param model
     * @param id
     * @return
     */
    @RequestMapping(value = "/project/lookProjectLabel")
    public String lookProjectLabel(Model model, Integer id) {
        List<ProjectLabel> projectLabelList = projectService.getProjectLabels(id);
        // 合并项目节点
        List<ProjectLabelParentBean> list = getProjectLabelBean(projectLabelList);
        List<ProjectLabel> normalList = getNormalLabels(projectLabelList);
        model.addAttribute("projectLabelList", list);
        model.addAttribute("normalList", normalList);
        return "project/viewProjectLabelInfo";
    }

    private List<ProjectLabel> getNormalLabels(List<ProjectLabel> projectLabelList) {
        List<ProjectLabel> normalList = new ArrayList<>();
        for (ProjectLabel projectLabel : projectLabelList) {
            if (projectLabel.getParentLevelTwoId() == null) {
                normalList.add(projectLabel);
            }
        }
        return normalList;
    }

    /**
     * 已相同父节点的节点放在一起
     *
     * @param paramList
     * @return
     */
    private List<ProjectLabelParentBean> getProjectLabelBean(List<ProjectLabel> paramList) {
        List<ProjectLabelParentBean> list = new ArrayList<>();
        Set<Integer> set = new TreeSet<>();
        paramList.forEach(projectLabel -> {
            if (projectLabel.getParentLevelTwoId() != null) {
                set.add(projectLabel.getParentLevelTwoId());
            }
        });
        for (Integer integer : set) {
            List<ProjectLabel> temp = new ArrayList<>();
            for (int i = 0; i < paramList.size(); i++) {
                if (paramList.get(i).getParentLevelTwoId() != null && paramList.get(i).getParentLevelTwoId().equals(integer)) {
                    temp.add(paramList.get(i));
                }
            }
            ProjectLabelParentBean projectLabelParentBean = new ProjectLabelParentBean();
            Collections.reverse(temp);
            projectLabelParentBean.setProjectLabelList(temp);
            list.add(projectLabelParentBean);
        }
        return list;
    }

